// Import the necessary modules.
// @flow
import type {
  $Response,
  $Request,
  NextFunction
} from 'express'
import type { MongooseModel } from 'mongoose'

import IContentController from './IContentController'
import type ContentService from './ContentService'

/**
 * Base class for getting content from endpoints.
 * @implements {IContentController}
 * @type {BaseContentController}
 */
export default class BaseContentController extends IContentController {

  /**
   * The base path for the routes.
   * @type {string}
   */
  basePath: string

  /**
   * The service of the content controller.
   * @type {ContentService}
   */
  service: ContentService

  /**
   * Create a new base content controller.
   * @param {!Object} options - The options for the base content controller.
   * @param {!string} options.basePath - The base path for the routes.
   * @param {!ContentService} options.service - The service for the content
   * controller.
   */
  constructor({basePath, service}: Object): void {
    super()

    /**
     * The base path for the routes.
     * @type {string}
     */
    this.basePath = basePath
    /**
     * The service of the content controller.
     * @type {ContentService}
     */
    this.service = service
  }

  /**
   * Default method to register the routes.
   * @override
   * @param {!Object} router - The router to register the routes to.
   * @param {!PopApi} PopApi - The PopApi instance.
   * @returns {undefined}
   */
  registerRoutes(router: Object, PopApi: any): void {
    const t = this.basePath

    router.get(`/${t}s`, this.getContents.bind(this))
    router.get(`/${t}s/:page`, this.getPage.bind(this))
    router.get(`/${t}/:id`, this.getContent.bind(this))
    router.post(`/${t}s`, this.createContent.bind(this))
    router.put(`/${t}/:id`, this.updateContent.bind(this))
    router.get(`/random/${t}`, this.getRandomContent.bind(this))
    if (typeof router.delete === 'function') {
      router.delete(`/${t}/:id`, this.deleteContent.bind(this))
    } else {
      router.del(`/${t}/:id`, this.deleteContent.bind(this))
    }
  }

  /**
   * Check if the content is empty or the length of the content array is zero.
   * @param {!ServerResponse} res - The server response object.
   * @param {!Object|Array<Object>} content - The content to check.
   * @returns {Object} - Returns a 204 response if the content is empty, or a
   * 200 response with the content if it is not empty.
   */
  checkEmptyContent(res: $Response, content: any): Object {
    res.setHeader('Content-Type', 'application/json')
    if (!content || content.length === 0) {
      res.status(204)
      return res.send()
    }

    res.status(200)
    return res.send(content)
  }

  /**
   * Get all the available pages.
   * @override
   * @param {!IncomingMessage} req - The incoming message request object.
   * @param {!ServerResponse} res - The server response object.
   * @param {!Function} next - The next function to move to the next
   * middleware.
   * @returns {Promise<Array<string>, Error>} - A list of pages which are
   * available.
   */
  getContents(
    req: $Request,
    res: $Response,
    next: NextFunction
  ): Promise<Array<string> | mixed> {
    return this.service.getContents(`/${this.basePath}`)
      .then(content => this.checkEmptyContent(res, content))
      .catch(err => next(err))
  }

  /**
   * Default method to sort the items.
   * @override
   * @param {!string} sort - The property to sort on.
   * @param {!number} order - The way to sort the property.
   * @returns {Object} - The sort object.
   */
  sortContent(sort: string, order: number): Object {
    return {
      [sort]: order
    }
  }

  /**
   * Get content from one page.
   * @override
   * @param {!IncomingMessage} req - The incoming message request object.
   * @param {!ServerResponse} res - The server response object.
   * @param {!Function} next - The next function to move to the next
   * middleware.
   * @returns {Promise<Array<Object>, Error>} - The content of one page.
   */
  getPage(
    req: $Request,
    res: $Response,
    next: NextFunction
  ): Promise<Array<MongooseModel> | mixed> {
    const { page } = req.params
    const { sort, order } = req.query

    const o = parseInt(order, 10) ? parseInt(order, 10) : -1
    const s = typeof sort === 'string' ? this.sortContent(sort, o) : null

    return this.service.getPage(s, Number(page))
      .then(content => this.checkEmptyContent(res, content))
      .catch(err => next(err))
  }

  /**
   * Get a content item based on the id.
   * @override
   * @param {!IncomingMessage} req - The incoming message request object.
   * @param {!ServerResponse} res - The server response object.
   * @param {!Function} next - The next function to move to the next
   * middleware.
   * @returns {Promise<Object, Error>} - The details of a single content item.
   */
  getContent(
    req: $Request,
    res: $Response,
    next: NextFunction
  ): Promise<MongooseModel | mixed> {
    return this.service.getContent(req.params.id)
      .then(content => this.checkEmptyContent(res, content))
      .catch(err => next(err))
  }

  /**
   * Create a new content item.
   * @override
   * @param {!IncomingMessage} req - The incoming message request object.
   * @param {!ServerResponse} res - The server response object.
   * @param {!Function} next - The next function to move to the next
   * middleware.
   * @returns {Promise<Object, Error>} - The created content item.
   */
  createContent(
    req: $Request,
    res: $Response,
    next: NextFunction
  ): Promise<MongooseModel | mixed> {
    res.setHeader('Content-Type', 'application/json')
    return this.service.createContent(req.body)
      .then(content => res.send(content))
      .catch(err => next(err))
  }

  /**
   * Update the info of one content item.
   * @override
   * @param {!IncomingMessage} req - The incoming message request object.
   * @param {!ServerResponse} res - The server response object.
   * @param {!Function} next - The next function to move to the next
   * middleware.
   * @returns {Promise<Object, Error>} - The updated content item.
   */
  updateContent(
    req: $Request,
    res: $Response,
    next: NextFunction
  ): Promise<MongooseModel | mixed> {
    res.setHeader('Content-Type', 'application/json')
    return this.service.updateContent(req.params.id, req.body)
      .then(content => res.send(content))
      .catch(err => next(err))
  }

  /**
   * Delete a content item.
   * @override
   * @param {!IncomingMessage} req - The incoming message request object.
   * @param {!ServerResponse} res - The server response object.
   * @param {!Function} next - The next function to move to the next
   * middleware.
   * @returns {Promise<Object, Error>} - The deleted content item
   */
  deleteContent(
    req: $Request,
    res: $Response,
    next: NextFunction
  ): Promise<MongooseModel | mixed> {
    res.setHeader('Content-Type', 'application/json')
    return this.service.deleteContent(req.params.id)
      .then(content => res.send(content))
      .catch(err => next(err))
  }

  /**
   * Get a random item.
   * @override
   * @param {!IncomingMessage} req - The incoming message request object.
   * @param {!ServerResponse} res - The server response object.
   * @param {!Function} next - The next function to move to the next
   * middleware.
   * @returns {Promise<Object, Error>} - A random item.
   */
  getRandomContent(
    req: $Request,
    res: $Response,
    next: NextFunction
  ): Promise<MongooseModel | mixed> {
    return this.service.getRandomContent()
      .then(content => this.checkEmptyContent(res, content))
      .catch(err => next(err))
  }

}

// Import the necessary modules.
// @flow
import pMap from 'p-map'
/**
 * MongoDB object modeling designed to work in an asynchronous environment.
 * @external {MongooseModel} https://github.com/Automattic/mongoose
 */
import type { MongooseModel } from 'mongoose'

/**
 * ContentService class for the CRUD operations.
 * @type {ContentService}
 */
export default class ContentService {

  /**
   * The model of the service.
   * @type {MongooseModel}
   */
  Model: MongooseModel

  /**
   * The maximum items to display per page.
   * @type {number}
   */
  pageSize: number

  /**
   * Simple projection for showing multiple content items.
   * @type {Object}
   */
  projection: Object

  /**
   * The query of the service.
   * @type {Object}
   */
  query: Object

  /**
   * Create a new ContentService.
   * @param {!Object} options - The options for the content service.
   * @param {!MongooseModel} options.Model - The model of the service.
   * @param {!Object} options.projection - The projection of the service.
   * @param {!Object} options.query={} - The query of the service.
   * @param {!number} [options.pageSize=25] - The page size of the service.
   */
  constructor({
    Model,
    projection,
    query = {},
    pageSize = 25
  }: Object): void {
    /**
     * The item type of the service.
     * @type {MongooseModel}
     */
    this.Model = Model
    /**
     * The maximum items to display per page.
     * @type {number}
     */
    this.pageSize = pageSize
    /**
     * Simple projection for showing multiple content items.
     * @type {Object}
     */
    this.projection = projection
    /**
     * Query to only get the content items.
     * @type {Object}
     */
    this.query = query
  }

  /**
   * Get all the available pages.
   * @param {!string} [base=''] - The base of the url to display.
   * @returns {Promise<Array<string>, Error>} - A list of pages which are
   * available.
   */
  getContents(base: string = ''): Promise<Array<string>> {
    return this.Model.count(this.query).then(count => {
      const pages = Math.ceil(count / this.pageSize)
      const docs = []

      for (let i = 1; i < pages + 1; i++) {
        docs.push(`${base}/${i}`)
      }

      return docs
    })
  }

  /**
   * Get content from one page.
   * @param {?Object} sort - The sort object to sort and order content.
   * @param {!number} [p=1] - The page to get.
   * @param {!Object} [query=this.query] - A copy of the query object to
   * get the objects.
   * @returns {Promise<Array<MongooseModel>, Error>} - The content of one page.
   */
  getPage(
    sort?: Object | null,
    p?: number | string = 1,
    query?: Object = {
      ...this.query
    }
  ): Promise<Array<any>> {
    const page = !isNaN(p) ? Number(p) - 1 : 0
    const offset = page * this.pageSize

    let aggregateQuery = [{
      $match: query
    }, {
      $project: this.projection
    }]

    if (sort) {
      aggregateQuery = [...aggregateQuery, {
        $sort: sort
      }]
    }

    if (typeof p === 'string' && p.toLowerCase() === 'all') {
      return this.Model.aggregate(aggregateQuery)
    }

    aggregateQuery = [...aggregateQuery, {
      $skip: offset
    }, {
      $limit: this.pageSize
    }]

    return this.Model.aggregate(aggregateQuery)
  }

  /**
   * Get the content from the database with an id.
   * @param {!string} id - The id of the content to get.
   * @param {!Object} projection - The projection for the content.
   * @returns {Promise<MongooseModel, Error>} - The details of the content.
   */
  getContent(id: string, projection?: Object): Promise<any> {
    return this.Model.findOne({
      _id: id
    }, projection)
  }

  /**
   * Insert the content into the database.
   * @param {!Object} obj - The object to insert.
   * @returns {Promise<MongooseModel, Error>} - The created content.
   */
  createContent(obj: Object): Promise<any> {
    return new this.Model(obj).save()
  }

  /**
   * Insert multiple content models into the database.
   * @param {!Array<Object>} arr - The array of content to insert.
   * @returns {Promise<Array<MongooseModel>, Error>} - The inserted content.
   */
  createMany(arr: Array<Object>): Promise<Array<any>> {
    return pMap(arr, async obj => {
      const found = await this.Model.findOne({
        _id: obj.slug
      })

      return found
        ? this.updateContent(obj.slug, obj)
        : this.createContent(obj)
    }, {
      concurrency: 1
    })
  }

  /**
   * Update the content.
   * @param {!string} id - The id of the content to get.
   * @param {!Object} obj - The object to update.
   * @returns {Promise<MongooseModel, Error>} - The updated content.
   */
  updateContent(id: string, obj: Object): Promise<any> {
    return this.Model.findOneAndUpdate({
      _id: id
    }, new this.Model(obj), {
      upsert: true,
      new: true
    })
  }

  /**
   * Update multiple content models into the database.
   * @param {!Array<Object>} arr - The array of content to update.
   * @returns {Promise<Array<MongooseModel>, Error>} - The updated content.
   */
  updateMany(arr: Array<Object>): Promise<Array<any>> {
    return this.createMany(arr)
  }

  /**
   * Delete a content model.
   * @param {!string} id - The id of the content to delete.
   * @returns {Promise<MongooseModel, Error>} - The deleted content.
   */
  deleteContent(id: string): Promise<any> {
    return this.Model.findOneAndRemove({
      _id: id
    })
  }

  /**
   * Delete multiple content models from the database.
   * @param {!Array<Object>} arr - The array of content to delete.
   * @returns {Promise<Array<MongooseModel>, Error>} - The deleted content.
   */
  deleteMany(arr: Array<Object>): Promise<Array<any>> {
    return pMap(arr, obj => this.deleteContent(obj._id))
  }

  /**
   * Get random content.
   * @returns {Promise<MongooseModel, Error>} - Random content.
   */
  getRandomContent(): Promise<any> {
    return this.Model.aggregate([{
      $match: this.query
    }, {
      $project: this.projection
    }, {
      $sample: {
        size: 1
      }
    }, {
      $limit: 1
    }]).then(([ res ]) => res)
  }

}

// @flow

/**
 * Interface for the route controllers.
 * @interface
 * @type {IController}
 */
export default class IController {

  /**
   * Default method to register the routes.
   * @abstract
   * @param {!Object} router - The router to register the routes to.
   * @param {!PopApi} PopApi - The PopApi instance.
   * @throws {Error} - Using default method: 'registerRoutes'
   * @returns {undefined}
   */
  registerRoutes(router: Object, PopApi: any): void {
    throw new Error('Using default method: \'registerRoutes\'')
  }

}

// Import the necessary modules.
// @flow
import statusCodes from './statusCodes'

/**
 * Error class for the Api.
 * @extends {Error}
 * @type {ApiError}
 */
export default class ApiError extends Error {

  /**
   * The name of the error
   * @type {string}
   */
  name: string

  /**
   * The message of the error
   * @type {string}
   */
  message: string

  /**
   * The status of the error
   * @type {string}
   */
  status: string

  /**
   * Whenever the error is public or not.
   * @type {boolean}
   */
  isPublic: boolean

  /**
   * Whenever the error is operational or not
   * @type {boolean}
   */
  isOperational: boolean

  /**
   * Create a new ApiError object.
   * @param {!Object} options - The options for the ApiError.
   * @param {!string} options.message - The message of the error.
   * @param {!string} options.status=500 - The status code of the error.
   * @param {!boolean} options.isPublic=false - Whenever the error is public or
   * not.
   */
  constructor({
    message,
    status = statusCodes.INTERNAL_SERVER_ERROR,
    isPublic = false
  }: Object): void {
    super(message)

    /**
     * The name of the error
     * @type {string}
     */
    this.name = this.constructor.name
    /**
     * The message of the error
     * @type {string}
     */
    this.message = message
    /**
     * The status of the error
     * @type {string}
     */
    this.status = status
    /**
     * Whenever the error is public or not.
     * @type {boolean}
     */
    this.isPublic = isPublic
    /**
     * Whenever the error is operational or not
     * @type {boolean}
     */
    this.isOperational = true

    Error.captureStackTrace(this, ApiError)
  }

}

// Import the necessary modules.
export ApiError from './ApiError'
export statusCodes from './statusCodes'

// Import the necessary modules.
// @flow
import { STATUS_CODES } from 'http'

/**
 * Swap the key-value pairs from the `http.STATUS_CODES` object.
 * @type {Object}
 */
const statusCodes: {
  [key: string]: number
} = Object.keys(STATUS_CODES).reduce((acc, current) => {
  const code = parseInt(current, 10)
  const message = STATUS_CODES[code]
    .replace(/'/g, '')
    .replace(/\s+/g, '_')
    .toUpperCase()
  acc[message] = code

  return acc
}, {})

/**
 * Export the status codes.
 * @type {Object}
 */
export default statusCodes

// Import the necessary modules.
// @flow
/* eslint-disable no-console */
/**
 * node.js command-line interfaces made easy
 * @external {Command} https://github.com/tj/commander.js
 */
import Command from 'commander'

/**
 * Class The class for the command line interface.
 * @type {Cli}
 */
export default class Cli {

  /**
   * The command line parser to process the Cli inputs.
   * @type {Command}
   */
  program: Object

  /**
   * The name of the Cli program.
   * @type {string}
   */
  name: string

  /**
   * The version of the Cli program.
   * @type {string}
   */
  version: string

  /**
   * Create a new Cli object.
   * @param {!PopApi} PopApi - The PopApi instance to bind the cli to.
   * @param {!Object} options - The options for the cli.
   * @param {?Array<string>} options.argv - The arguments to be parsed by
   * commander.
   * @param {!string} options.name - The name of the Cli program.
   * @param {!string} options.version - The version of the Cli program.
   * @throw {TypeError} - 'name' and 'version' are required options for the Cli
   * middleware!
   */
  constructor(PopApi: any, {argv, name, version}: Object): void {
    const { name: debugName } = this.constructor
    PopApi.debug(`Registering ${debugName} middleware with options: %o`, {
      argv,
      name,
      version
    })

    if (!name || !version) {
      throw new TypeError('\'name\' and \'version\' are required options for the Cli middleware!')
    }

    /**
     * The command line parser to process the Cli inputs.
     * @type {Command}
     */
    this.program = Command
    /**
     * The name of the Cli program.
     * @type {string}
     */
    this.name = name
    /**
     * The version of the Cli program.
     * @type {string}
     */
    this.version = version

    this.initOptions()
    this.program.on('--help', this.printHelp.bind(this))

    if (argv) {
      this.run(PopApi, argv)
    }
  }

  /**
   * Initiate the options for the Cli.
   * @returns {undefined}
   */
  initOptions(): void {
    return this.program.version(`${this.name} v${this.version}`)
      .option(
        '-m, --mode <type>',
        'Run the API in a particular mode.',
        /^(pretty|quiet|ugly)$/i
      )
  }

  /**
   * Get the help message.
   * @returns {Array<string>} - The help message to print.
   */
  getHelp(): Array<string> {
    return [
      '',
      '  Examples:',
      '',
      `    $ ${this.name} -m <pretty|quiet|ugly>`,
      `    $ ${this.name} --mode <pretty|quiet|ugly>`
    ]
  }

  /**
   * Method for displaying the --help option
   * @returns {undefined}
   */
  printHelp(): void {
    console.info(`${this.getHelp().join('\n')}\n`)
  }

  /**
   * Handle the --mode Cli option.
   * @param {?string} [m] - The mode to run the API in.
   * @returns {Object} - The options to pass to the Logger middleware.
   */
  mode(m?: string): Object {
    const testing = process.env.NODE_ENV === 'test'

    switch (m) {
      case 'quiet':
        return {
          pretty: false,
          quiet: true
        }
      case 'ugly':
        return {
          pretty: false,
          quiet: testing
        }
      case 'pretty':
      default:
        return {
          pretty: !testing,
          quiet: testing
        }
    }
  }

  /**
   * Run the Cli program.
   * @param {!PopApi} PopApi - The PopApi instance to bind the logger to.
   * @param {?Array<string>} argv - The arguments to be parsed by commander.
   * @returns {undefined}
   */
  run(PopApi: any, argv?: Array<string>): void {
    if (argv) {
      this.program.parse(argv)
    }

    if (this.program.mode) {
      PopApi.loggerArgs = this.mode(this.program.mode)
    } else {
      console.error('\n  error: no valid command given, please check below:')
      return this.program.help()
    }
  }

}

// Import the necessary modules.
// @flow
import mongoose from 'mongoose'
import { existsSync } from 'fs'
import {
  isAbsolute,
  join
} from 'path'
import { URL } from 'url'

import { executeCommand } from '../utils'

/**
 * Class for setting up a database connection.
 * @type {Database}
 */
export default class Database {

  /**
   * The name of the database. Default is the package name with the
   * environment mode.
   * @type {string}
   */
  database: string

  /**
   * The host of the server of the database. Default is `['localhost']`.
   * @type {Array<string>}
   */
  hosts: Array<string>

  /**
   * The port of the database. Default is `27017`.
   * @type {string}
   */
  dbPort: number

  /**
   * The username of the database. By default this is left empty.
   * @type {string}
   */
  username: string

  /**
   * The password of the database. By default this is left empty.
   * @type {string}
   */
  password: string

  /**
   * Create a new Database object.
   * @param {!PopApi} PopApi - The PopApi instance to bind the database to.
   * @param {!Object} options - The options for the database.
   * @param {!string} options.database - The arguments to be parsed by
   * @param {!Array<string>} [options.hosts=['localhost']] - The hosts for the
   * database connection.
   * @param {!number} [options.dbPort=27017] - The port for the database
   * connection.
   * @param {?string} [options.username] - The username for the database
   * connection.
   * @param {?string} [options.password] - The password for the database
   * connection.
   * @throws {TypeError} - 'database' is a required option for the Database
   * middleware!
   */
  constructor(PopApi: any, {
    database,
    hosts = ['localhost'],
    dbPort = 27017,
    username,
    password
  }: Object): void {
    const { name: debugName } = this.constructor
    PopApi.debug(`Registering ${debugName} middleware with options: %o`, {
      database,
      hosts,
      dbPort,
      username,
      password
    })

    if (!database) {
      throw new TypeError('\'database\' is a required option for the Database middleware!')
    }

    process.env.NODE_ENV = process.env.NODE_ENV || 'development'

    const {
      MONGO_PORT_27017_TCP_ADDR,
      MONGO_PORT_27017_TCP_PORT,
      NODE_ENV
    } = process.env

    this.database = `${database}-${NODE_ENV}`
    this.hosts = MONGO_PORT_27017_TCP_ADDR
      ? [MONGO_PORT_27017_TCP_ADDR]
      : hosts
    this.dbPort = Number(MONGO_PORT_27017_TCP_PORT) || dbPort
    this.username = username || ''
    this.password = password || ''

    PopApi.database = this
  }

  /**
   * Connection and configuration of the database.
   * @returns {Promise<undefined, Error>} - The promise to connect to the
   * database.
   */
  connect(): Promise<void> {
    const uri = new URL(`mongodb://${this.username}:${this.password}@${this.hosts.join(',')}:${this.dbPort}/${this.database}`)

    return mongoose.connect(uri.href)
  }

  /**
   * Disconnect from the database.
   * @returns {Promise<undefined, Error>} - The promise to disconnect from
   * the database.
   */
  disconnect(): Promise<void> {
    return mongoose.disconnect()
  }

  /**
   * Export a JSON file collection.
   * @param {!string} collection - The collection to export.
   * @param {!string} outputFile - The path of the output file of the export.
   * @returns {Promise<string, undefined>} - The promise to export a
   * collection.
   */
  exportFile(
    collection: string,
    outputFile: string
  ): Promise<string | void> {
    return executeCommand('mongoexport', [
      '-d', this.database,
      '-c', `${collection}s`,
      '-o', outputFile
    ])
  }

  /**
   * Import a JSON file collection.
   * @param {!string} collection - The collection to import.
   * @param {!string} jsonFile - The JSON file to import.
   * @returns {Promise<string, undefined>} - The promise to import a
   * collection.
   */
  importFile(
    collection: string,
    jsonFile: string
  ): Promise<string | void> {
    const file = !isAbsolute(jsonFile)
      ? jsonFile
      : join(...[__dirname, '..', '..', jsonFile])

    if (!existsSync(file)) {
      const err = new Error(`no such file found for '${file}'`)
      return Promise.reject(err)
    }

    return executeCommand('mongoimport', [
      '-d', this.database,
      '-c', `${collection}s`,
      '--file', jsonFile,
      '--upsert'
    ])
  }

}

// Import the necessary modules.
// @flow
import cluster from 'cluster'
/** @external {http~Server} https://nodejs.org/dist/latest/docs/api/http.html#http_class_http_server */
import http from 'http'
import { cpus } from 'os'

import type Database from './Database'

/**
 * Class for starting the API.
 * @type {Server}
 */
export default class HttpServer {

  /**
   * the http server object.
   * @type {http~Server}
   * @see https://nodejs.org/api/http.html#http_http_createserver_requestlistener
   */
  server: Server

  /**
   * The port on which the API will run on. Default is `5000`.
   * @type {number}
   */
  serverPort: number

  /**
   * The amount of workers on the cluster.
   * @type {number}
   */
  workers: number

  /**
   * Create a new Server object.
   * @param {!PopApi} PopApi - The PopApi instance to bind the server to.
   * @param {!Object} options - The options for the server.
   * @param {!Express} options.app - The application instance to create a
   * server for.
   * @param {!number} [options.serverPort=process.env.PORT] - The port the API
   * will run on.
   * @param {!number} [options.workers=2] - The amount of workers to fork.
   * @throws {TypeError} - 'app' is a required option for the HttpServer
   * middleware!
   */
  constructor(PopApi: any, {
    app,
    serverPort = process.env.PORT || 5000,
    workers = 2
  }: Object): void {
    const { name: debugName } = this.constructor
    PopApi.debug(`Registering ${debugName} middleware with options: %o`, {
      serverPort,
      workers
    })

    if (!app) {
      throw new TypeError('\'app\' is a required option for the HttpServer middleware!')
    }

    /**
     * The amount of workers on the cluster.
     * @type {number}
     */
    this.server = typeof app === 'function' ? http.createServer(app) : app
    /**
     * The port on which the API will run on. Default is `5000`.
     * @type {number}
     */
    this.serverPort = serverPort
    /**
     * The amount of workers on the cluster.
     * @type {number}
     */
    this.workers = workers
    this.setupApi(app)

    PopApi.server = this
  }

  /**
   * For the workers.
   * @returns {undefined}
   */
  forkWorkers(): void {
    for (let i = 0; i < Math.min(cpus().length, this.workers); i++) {
      cluster.fork()
    }
  }

  /**
   * Handle the errors for workers.
   * @returns {undefined}
   */
  workersOnExit(): void {
    cluster.on('exit', ({ process }) => {
      const msg = `Worker '${process.pid}' died, spinning up another!`
      logger.error(msg)

      cluster.fork()
    })
  }

  /**
   * Method to setup the cron job.
   * @param {!Express} app - The application instance to create a server for.
   * @returns {undefined}
   */
  setupApi(app: Object): void {
    if (cluster.isWorker || this.workers === 0) {
      this.server = app.listen(this.serverPort)
    }

    if (cluster.isMaster || this.workers === 0) {
      this.forkWorkers()
      this.workersOnExit()

      logger.info(`API started on port: ${this.serverPort}`)
    }
  }

  /**
   * Method to stop the API from running.
   * @param {!Database} database - The database connection to close.
   * @param {?Function} [done=() => {}] - Function to exit the API.
   * @returns {undefined}
   */
  closeApi(database: Database, done: Function = () => {}): void {
    this.server.close(() => {
      database.disconnect().then(() => {
        logger.info('Closed out remaining connections.')
        done()
      })
    })
  }

}

// Import the necessary modules.
// @flow
import { join } from 'path'
/**
 * express.js middleware for winstonjs
 * @external {ExpressWinston} https://github.com/bithavoc/express-winston
 */
import {
  logger as httpLogger,
  requestWhitelist,
  responseWhitelist
} from '@chrisalderson/express-winston'
import type { Middleware } from 'express'
/**
 * a multi-transport async logging library for node.js
 * @external {Winston} https://github.com/winstonjs/winston
 */
import {
  type createLogger as Winston,
  loggers,
  format,
  transports
} from 'winston'

import { padStart } from './internal'

/**
 * Class for setting up the logger.
 * @type {Logger}
 */
export default class Logger {

  /**
   * The file transport for the logger.
   * @type {Object}
   */
  static fileTransport: Object

  /**
   * The log levels the logger middleware will be using.
   * @type {Object}
   */
  levels: Object

  /**
   * The name of the log file.
   * @type {string}
   */
  name: string

  /**
   * The directory where the log file will be stored.
   * @type {string}
   */
  logDir: string

  /**
   * Create a new Logger object.
   * @param {!PopApi} PopApi - The PopApi instance to bind the logger to.
   * @param {!Object} options - The options for the logger.
   * @param {!string} options.name - The name of the log file.
   * @param {?boolean} [options.pretty] - Pretty mode for output with colors.
   * @param {?boolean} [options.quiet] - No output.
   * @throws {TypeError} - 'name' and 'logDir' are required options for the
   * Logger middleware!
   */
  constructor(PopApi: any, {name, logDir, pretty, quiet}: Object): void {
    const { name: debugName } = this.constructor
    PopApi.debug(`Registering ${debugName} middleware with options: %o`, {
      name,
      logDir,
      pretty,
      quiet
    })

    if (!name || !logDir) {
      throw new TypeError('\'name\' and \'logDir\' are required options for the Logger middleware!')
    }

    /**
     * The log levels the logger middleware will be using.
     * @type {Object}
     */
    this.levels = {
      error: 0,
      warn: 1,
      info: 2,
      debug: 3
    }
    /**
     * The name of the log file.
     * @type {string}
     */
    this.name = name
    /**
     * The directory where the log file will be stored.
     * @type {string}
     */
    this.logDir = logDir

    // Apply the polyfill if necessary.
    // @flow-ignore
    String.prototype.padStart = String.prototype.padStart || padStart // eslint-disable-line no-extend-native

    global.logger = this.getLogger('logger', pretty, quiet)
    if (process.env.NODE_ENV !== 'test') {
      PopApi.httpLogger = this.getLogger('http', pretty, quiet)
    }
  }

  /**
   * Get the color of the output based on the log level.
   * @param {?string} [level=info] - The log level.
   * @returns {string} - A color based on the log level.
   */
  getLevelColor(level: string = 'info'): string {
    const colors = {
      error: '\x1b[31m',
      warn: '\x1b[33m',
      info: '\x1b[36m',
      debug: '\x1b[34m'
    }

    return colors[level]
  }

  /**
   * Update the message property and add the splat property to the info
   * object for interpolation.
   * @param {Object} info - The info object processed by logform.
   * @returns {Object} - The info object with the modified message and splat
   * property.
   */
  prettyPrintConsole(info: Object): Object {
    const { level, message, timestamp } = info
    const c = this.getLevelColor(level)

    info.splat = [
      timestamp,
      level.toUpperCase().padStart(5),
      this.name.padStart(2),
      process.pid,
      message
    ]
    info.message = `\x1b[0m[%s] ${c}%s:\x1b[0m %s/%d: \x1b[36m%s\x1b[0m`

    return info
  }

  /**
   * Get the message string from the info object.
   * @param {Object} info - The info object processed by logform.
   * @returns {string} - The message string to print out of the info object.
   */
  _getMessage(info: Object): string {
    return info.message
  }

  /**
   * Formatter method which formats the output to the console.
   * @returns {Object} - The formatter for the console transport.
   */
  consoleFormatter(): Object {
    return format.combine(
      format.timestamp(),
      format.printf(this.prettyPrintConsole.bind(this)),
      format.splat(),
      format.printf(this._getMessage)
    )
  }

  /**
   * Formatter method which formats the output to the log file.
   * @returns {Object} - The formatter for the file transport.
   */
  fileFormatter(): Object {
    return format.combine(
      format.timestamp(),
      format.printf(info => {
        Object.assign(info, {
          name: this.name,
          pid: process.pid
        })
        return info
      }),
      format.json()
    )
  }

  /**
   * Create a Console transport.
   * @param {?boolean} [pretty] - Pretty mode for output with colors.
   * @returns {Object} - A configured Console transport.
   */
  getConsoleTransport(pretty?: boolean): Object {
    const f = pretty
      ? this.consoleFormatter()
      : format.simple()

    return new transports.Console({
      name: this.name,
      format: f
    })
  }

  /**
   * Create a File transport.
   * @param {!string} file - The file to log the output to.
   * @returns {Object} - A configured File transport.
   */
  getFileTransport(file: string): Object {
    if (!Logger.fileTransport) {
      Logger.fileTransport = new transports.File({
        level: 'warn',
        filename: join(...[
          this.logDir,
          `${file}.log`
        ]),
        format: this.fileFormatter(),
        maxsize: 5242880,
        handleExceptions: true
      })
    }

    return Logger.fileTransport
  }

  /**
   * Create a logger instance.
   * @param {!string} suffix - The suffix for the log file.
   * @param {?boolean} [pretty] - Pretty mode for output with colors.
   * @returns {Winston} - A configured logger instance.
   */
  createLoggerInstance(suffix: string, pretty?: boolean): Winston {
    const id = `${this.name}-${suffix}`

    return loggers.add(id, {
      levels: this.levels,
      level: 'debug',
      exitOnError: false,
      transports: [
        this.getConsoleTransport(pretty),
        this.getFileTransport(id)
      ]
    })
  }

  /**
   * Get the log message for Http logger.
   * @param {!Object} req - The request object to log.
   * @param {!Object} res - The response object to log.
   * @returns {string} - The HtpP log message to print.
   */
  getHttpLoggerMessage(req: $Response, res: $Response): string {
    return `HTTP ${req.method} ${req.url} ${res.statusCode} ${res.responseTime}ms`
  }

  /**
   * Create a Http logger instance.
   * @param {?boolean} [pretty] - Pretty mode for output with colors.
   * @returns {ExpressWinston} - A configured Http logger instance.
   */
  createHttpLogger(pretty?: boolean): Middleware {
    const logger = this.createLoggerInstance('http', pretty)
    const options: {
      [key: string]: mixed
    } = {
      winstonInstance: logger,
      meta: true,
      msg: this.getHttpLoggerMessage,
      statusLevels: true
    }

    if (process.env.NODE_ENV === 'development') {
      const { Console } = transports
      logger.add(new Console({
        name: this.name,
        format: format.json({
          space: 2
        })
      }))

      options.requestWhitelist = [].concat(requestWhitelist, 'body')
      options.responseWhitelist = [].concat(responseWhitelist, 'body')
    }

    return httpLogger(options)
  }

  /**
   * Method to create a global logger object based on the properties of the
   * Logger class.
   * @param {?boolean} [pretty] - Pretty mode for output with colors.
   * @param {?boolean} [quiet] - No output.
   * @returns {Object|Winston} - A configured logger.
   */
  createLogger(pretty?: boolean, quiet?: boolean): Object | Winston {
    const logger = this.createLoggerInstance('app', pretty)

    if (quiet) {
      Object.keys(this.levels).map(level => {
        logger[level] = () => {}
      })
    }

    return logger
  }

  /**
   * Get a logger object based on the choice.
   * @param {?string} [type] - The choice for the logger object.
   * @param {?boolean} [pretty] - Pretty mode for output with colors.
   * @param {?boolean} [quiet] - No output.
   * @returns {Middleware|Winston|undefined} - The logger object.
   */
  getLogger(
    type?: string,
    pretty?: boolean,
    quiet?: boolean
  ): Middleware | Winston | void {
    if (!type) {
      return undefined
    }

    const t = type.toUpperCase()

    switch (t) {
      case 'HTTP':
        return this.createHttpLogger(pretty)
      case 'LOGGER':
        return this.createLogger(pretty, quiet)
      default:
        return undefined
    }
  }

}

// Import the necessary modules.
// @flow
import bodyParser from 'body-parser'
import compress from 'compression'
import helmet from 'helmet'
import type {
  $Application,
  $Request,
  $Response,
  NextFunction
} from 'express'
import responseTime from 'response-time'
import { STATUS_CODES as statusMessages } from 'http'

import {
  ApiError,
  statusCodes
} from '../helpers'

/**
 * Class for setting up the Routes.
 * @type {Routes}
 */
export default class Routes {

  /**
   * Create a new Routes object.
   * @param {!PopApi} PopApi - The PopApi instance to bind the routes to.
   * @param {!Object} options - The options for the routes.
   * @param {!Express} options.app - The application instance to add middleware
   * and bind the routes to.
   * @param {?Array<Object>} options.controllers - The controllers to register.
   * @throws {TypeError} - 'app' are required options for the routes
   * middleware!
   */
  constructor(PopApi: any, {app, controllers}: Object): void {
    const { name: debugName } = this.constructor
    PopApi.debug(`Registering ${debugName} middleware with options: %o`, {
      controllers
    })

    if (!app) {
      throw new TypeError('\'app\' is a required option for the Routes middleware!')
    }

    this.setupRoutes(app, PopApi, controllers)
  }

  /**
   * Register the controllers found in the controllers directory.
   * @param {!Express} app - The application instance to register the routers
   * to.
   * @param {!PopApi} PopApi - The PopApi instance to bind the routes to.
   * @param {!Array<Object>} controllers - The controllers to register.
   * @returns {undefined}
   */
  registerControllers(
    app: $Application,
    PopApi: any,
    controllers: Array<Object>
  ): void {
    controllers.forEach(c => {
      const { Controller, args } = c
      const controller = new Controller(args)

      PopApi.debug(`Registering ${Controller.name} route controller`)
      controller.registerRoutes(app, PopApi)
    })
  }

  /**
   * Convert the thrown errors to an instance of ApiError.
   * @param {!Error} err - The caught error.
   * @param {!IncomingMessage} req - The incoming message request object.
   * @param {!ServerResponse} res - The server response object.
   * @param {!Function} next - The next function to move to the next
   * middleware.
   * @returns {ApiError} - The converted error.
   */
  convertErrors(
    err: Error,
    req: $Request,
    res: $Response,
    next: NextFunction
  ): mixed {
    if (!(err instanceof ApiError)) {
      const error = new ApiError({
        message: err.message
      })
      return next(error)
    }

    return next(err)
  }

  /**
   * Catch the 404 errors.
   * @param {!IncomingMessage} req - The incoming message request object.
   * @param {!ServerResponse} res - The server response object.
   * @param {!Function} next - The next function to move to the next
   * middleware.
   * @returns {ApiError} - A standard 404 error.
   */
  setNotFoundHandler(
    req: $Request,
    res: $Response,
    next: NextFunction
  ): mixed {
    const err = new ApiError({
      message: 'Api not found',
      status: statusCodes.NOT_FOUND
    })

    return next(err)
  }

  /**
   * Error handler middleware
   * @param {!ApiError} err - The caught error.
   * @param {!IncomingMessage} req - The incoming message request object.
   * @param {!ServerResponse} res - The server response object.
   * @param {!Function} next - The next function to move to the next
   * middleware.
   * @returns {Object} - The error object.
   */
  setErrorHandler(
    err: ApiError,
    req: $Request,
    res: $Response,
    next: NextFunction
  ): Object {
    const { status } = err
    const body: {
      [key: string]: string
    } = {
      message: err.isPublic
        ? err.message
        : `${status} ${statusMessages[status]}`
    }

    if (process.env.NODE_ENV === 'development') {
      body.stack = err.stack
    }

    res.setHeader('Content-Type', 'application/json')
    res.status(status)
    return res.send(body)
  }

  /**
   * Remove security sensitive headers.
   * @see https://github.com/shieldfy/API-Security-Checklist#output
   * @param {!IncomingMessage} req - The incoming message request object.
   * @param {!ServerResponse} res - The server response object.
   * @param {!Function} next - The next function to move to the next
   * middleware.
   * @returns {undefined}
   */
  removeServerHeader(
    req: $Request,
    res: $Response,
    next: NextFunction
  ): mixed {
    res.removeHeader('Server')
    return next()
  }

  /**
   * Hook method for setting up middleware pre setting up the routes.
   * @param {!Express} app - The application instance to add middleware to.
   * @returns {undefined}
   */
  preRoutes(app: $Application): void {
    // Enable parsing URL encoded bodies.
    app.use(bodyParser.urlencoded({
      extended: true
    }))

    // Enable parsing JSON bodies.
    app.use(bodyParser.json())

    // Enables compression of response bodies.
    app.use(compress({
      threshold: 1400,
      level: 4,
      memLevel: 3
    }))

    // Enable response time tracking for HTTP request.
    app.use(responseTime())

    // Set and remove the security sensitive headers.
    app.use(helmet())
    app.use(helmet.contentSecurityPolicy({
      directives: {
        defaultSrc: ['\'none\'']
      }
    }))
    app.use(this.removeServerHeader)
  }

  /**
   * Hook method for setting up middleware post setting up the routes.
   * @param {!Express} app - The application instance to add middleware to.
   * @returns {undefined}
   */
  postRoutes(app: $Application): void {
    // Convert the caught errors to the ApiError instance.
    app.use(this.convertErrors)

    // Set the default not found handling middleware.
    app.use(this.setNotFoundHandler)

    // Set the default error handling middleware.
    app.use(this.setErrorHandler)
  }

  /**
   * Setup the application service.
   * @param {!Express} app - The application instance to add middleware and
   * bind the routes to.
   * @param {!PopApi} PopApi - The PopApi instance to bind the routes to.
   * @param {?Array<Object>} [controllers] - The controllers to register.
   * @returns {undefined}
   */
  setupRoutes(
    app: $Application,
    PopApi?: any,
    controllers?: Array<Object>
  ): void {
    // Pre routes hook.
    this.preRoutes(app)

    // Enable HTTP request logging.
    if (PopApi && PopApi.httpLogger) {
      app.use(PopApi.httpLogger)
    }

    // Register the controllers.
    if (controllers) {
      this.registerControllers(app, PopApi, controllers)
    }

    // Post routes hook.
    this.postRoutes(app)
  }

}

// @flow
/**
 * Polyfill for String.prototype.padStart.
 * @link https://developer.mozzila.org/en-US/docs/Web/JAvaScript/Refernce/Global_Objects/String/padStart
 * @param {!number} targetLength - The length of the resulting string once the
 * current string had been padded.
 * @param {?string} padString - The string to pad the currnet string with.
 * @returns {string} - A string of the specified length with the pad string
 * applied to the start.
 */
export default function (targetLength: number, padString?: string): string {
  let targetLen: number = targetLength >> 0
  let padStr: string = String(padString || ' ')

  if (this.length > targetLen) {
    return String(this)
  }

  targetLen = targetLen - this.length
  if (targetLen > padStr.length) {
    padStr += padStr.repeat(targetLen / padStr.length)
  }

  return padStr.slice(0, targetLen) + String(this)
}

// Import the necessary modules.
// @flow
import del from 'del'
import mkdirp from 'mkdirp'
import { existsSync } from 'fs'
import { spawn } from 'child_process'

/**
 * Create a temporary directory for files for the API.
 * @param {!string} path - The path to the directory to create.
 * @returns {Promise<string, Error>} - The path to the created directory.
 */
export async function createTemp(path: string): Promise<string | Error> {
  if (existsSync(path)) {
    await del([`${path}/**`]).then(([ res ]) => res)
  }

  return new Promise(resolve => {
    mkdirp.sync(path)
    return resolve(path)
  })
}

/**
 * Execute a command from within the root folder.
 * @param {!string} cmd - The command to execute.
 * @param {?Array<string>} args - The arguments passed to the command.
 * @returns {Promise<string, Error>} - The output of the command.
 */
export function executeCommand(
  cmd: string,
  args: Array<string>
): Promise<void> {
  return new Promise((resolve, reject) => {
    const res = spawn(cmd, args)

    res.stdout.on('data', data => resolve(data.toString()))
    res.on('error', reject)
    res.on('close', code => {
      if (code === 0) {
        return resolve()
      }

      const err = new Error(`${cmd} exited with code: ${code}`)
      return reject(err)
    })
  })
}

# pop-api

[![Build Status](https://travis-ci.org/popcorn-official/pop-api.svg?branch=master)](https://travis-ci.org/popcorn-official/pop-api)
[![Windows Build](https://img.shields.io/appveyor/ci/chrisalderson/pop-api/master.svg?label=windows)](https://ci.appveyor.com/project/ChrisAlderson/pop-api)
[![Coverage Status](https://coveralls.io/repos/github/popcorn-official/pop-api/badge.svg?branch=master)](https://coveralls.io/github/popcorn-official/pop-api?branch=master)
[![Dependency Status](https://david-dm.org/popcorn-official/pop-api.svg)](https://david-dm.org/popcorn-official/pop-api)
[![devDependencies Status](https://david-dm.org/popcorn-official/pop-api/dev-status.svg)](https://david-dm.org/popcorn-official/pop-api?type=dev)

## Features

The pop-api project aims to provide the core modules for the
[`popcorn-api`](https://github.com/popcorn-official/popcorn-api) project, but
can also be used for other purposes by using middleware.
 - Cli middleware for reading user input with [`commander.js`](https://github.com/tj/commander.js).
 - Database middleware for connection to MongoDB through [`mongoose`](https://github.com/Automattic/mongoose).
 - Logging of routes and other information using [`winston`](https://github.com/winstonjs/winston).
 - Uses [`express`](https://github.com/expressjs/express) under the hood with:
   - Body middleware with [`body-parser`](https://github.com/expressjs/body-parser)
   - Error handling
   - Security middleware with [`helmet`](https://github.com/helmetjs/helmet)
 - Interface for registering routes for [`express`](https://github.com/expressjs/express).
 - Data Access Layer (DAL) class for standard CRUD operations.
 - Route controller to handle routes for your content.

## Installation

```
 $ npm install --save pop-api
```

## Documentation

 - [General documentation](https://popcorn-official.github.io/pop-api/manual/index.html)
 - [Api docs](https://popcorn-official.github.io/pop-api/identifiers.html)
